<span class="line"><span style="color:#E1E4E8;">  &lt;</span><span style="color:#85E89D;">img</span><span style="color:#B392F0;"> :src</span><span style="color:#E1E4E8;">=</span><span style="color:#9ECBFF;">&quot;imageUrl&quot;</span><span style="color:#B392F0;"> alt</span><span style="color:#E1E4E8;">=</span><span style="color:#9ECBFF;">&quot;example&quot;</span><span style="color:#E1E4E8;"> /&gt;</span></span>
